mapper.insert:

	SELECT NVL(MAX(cus_num), 0) + 1 FROM cus

INSERT INTO cus (cus_num, cus_nm, zipcode, addr1, addr2, pne, pwd, cus_detail, cus_image)
VALUES
(
    #{cusNum}
    ,#{cusNm}
    ,#{zipcode}
    ,#{addr1}
    ,#{addr2}
    ,#{pne}
    ,#{pwd}
    ,#{cusDetail}
    ,#{cusImageUpload}
)

mapper.select:
SELECT CUS_NUM, CUS_NM, ZIPCODE, ADDR1, ADDR2, PNE, PWD, CUS_DETAIL, CUS_IMAGE
FROM CUS
WHERE CUS_NUM = #{cusNum}

mapper.update:
UPDATE 	CUS
SET		CUS_NM = #{cusNm}, ZIPCODE = #{zipcode}, ADDR1=#{addr1},ADDR2=#{addr2},PNE = #{pne},CUS_DETAIL=#{cusDetail}
WHERE 	CUS_NUM = #{cusNum}

UPDATE 	CUS
SET		CUS_NM = #{cusNm}, ZIPCODE = #{zipcode}, ADDR1=#{addr1},ADDR2=#{addr2},PNE = #{pne},CUS_DETAIL=#{cusDetail},CUS_IMAGE=#{cusImageUpload}
WHERE 	CUS_NUM = #{cusNum}

mapper.select:
SELECT COUNT(*) CNT
FROM CAR
WHERE CUS_NUM = #{cusNum}
